import React, { useState, useEffect } from 'react';
import { 
  Download, 
  Plus, 
  Eye, 
  Target,
  Building,
  MapPin,
  Mail,
  Phone,
  TrendingUp,
  CheckCircle,
  Clock,
  Edit,
  Tag
} from 'lucide-react';
import { getLeads, updateLead, addTag, addNote, startLeadProspecting } from '../services/api';
import { exportToCSV, exportToExcel } from '../utils/exportUtils';
import toast from 'react-hot-toast';

const LeadProspecting = () => {
  const [leads, setLeads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [prospectingLoading, setProspectingLoading] = useState(false);
  const [activeProspecting, setActiveProspecting] = useState(false);
  const [prospectingProgress, setProspectingProgress] = useState(0);
  const [prospectingStatus, setProspectingStatus] = useState('');
  const [generatedLeads, setGeneratedLeads] = useState([]);
  const [filters, setFilters] = useState({
    industry: '',
    companySize: '',
    location: '',
    status: '',
    qualityScore: '',
    source: ''
  });
  const [selectedLead, setSelectedLead] = useState(null);
  const [showLeadModal, setShowLeadModal] = useState(false);
  const [showTagModal, setShowTagModal] = useState(false);
  const [showNoteModal, setShowNoteModal] = useState(false);
  const [showProspectingModal, setShowProspectingModal] = useState(false);
  const [prospectingForm, setProspectingForm] = useState({
    query: '',
    industry: '',
    location: '',
    companySize: '',
    maxResults: 100,
    includeEmails: true,
    enrichData: true
  });

  // Predefined industry options
  const industryOptions = [
    'Technology',
    'Healthcare',
    'Finance',
    'Manufacturing',
    'Retail',
    'Education',
    'Real Estate',
    'Consulting',
    'Marketing',
    'Legal',
    'Construction',
    'Transportation',
    'Energy',
    'Media',
    'Non-profit',
    'Government',
    'Agriculture',
    'Entertainment',
    'Sports',
    'Other'
  ];

  // Predefined location options (major cities and countries)
  const locationOptions = [
    'United States',
    'United Kingdom',
    'Canada',
    'Australia',
    'Germany',
    'France',
    'Netherlands',
    'Sweden',
    'Switzerland',
    'Singapore',
    'Japan',
    'South Korea',
    'India',
    'Brazil',
    'Mexico',
    'New York',
    'Los Angeles',
    'Chicago',
    'Houston',
    'Phoenix',
    'Philadelphia',
    'San Antonio',
    'San Diego',
    'Dallas',
    'San Jose',
    'London',
    'Manchester',
    'Birmingham',
    'Leeds',
    'Liverpool',
    'Toronto',
    'Vancouver',
    'Montreal',
    'Calgary',
    'Ottawa',
    'Sydney',
    'Melbourne',
    'Brisbane',
    'Perth',
    'Adelaide',
    'Berlin',
    'Munich',
    'Hamburg',
    'Cologne',
    'Frankfurt',
    'Paris',
    'Lyon',
    'Marseille',
    'Toulouse',
    'Nice',
    'Amsterdam',
    'Rotterdam',
    'The Hague',
    'Utrecht',
    'Eindhoven'
  ];

  // Company size options
  const companySizeOptions = [
    '1-10',
    '11-50',
    '51-200',
    '201-500',
    '501-1000',
    '1001-5000',
    '5000+'
  ];

  // Source options
  const sourceOptions = [
    'Google',
    'LinkedIn',
    'Yellow Pages',
    'Email Scraping',
    'Manual Entry',
    'Import',
    'API'
  ];

  useEffect(() => {
    fetchLeads();
  }, []);

  const fetchLeads = async () => {
    try {
      setLoading(true);
      const response = await getLeads();
      
      // The backend returns { leads: [...], pagination: {...} }
      // The API interceptor extracts response.data, so we get the full response object
      let leadsData = [];
      if (response && response.leads && Array.isArray(response.leads)) {
        leadsData = response.leads;
      } else if (Array.isArray(response)) {
        // Fallback: if response is directly an array
        leadsData = response;
      } else {
        console.warn('Unexpected leads response format:', response);
        leadsData = [];
      }
      
      setLeads(leadsData);
    } catch (error) {
      toast.error('Failed to fetch leads');
      console.error('Error fetching leads:', error);
      setLeads([]); // Set empty array on error
    } finally {
      setLoading(false);
    }
  };

  const handleProspectingSubmit = async (e) => {
    e.preventDefault();
    setProspectingLoading(true);
    
    try {
      // Map frontend fields to backend API fields
      const apiData = {
        query: prospectingForm.query,
        industry: prospectingForm.industry,
        location: prospectingForm.location,
        companySize: prospectingForm.companySize,
        maxResults: prospectingForm.maxResults,
        includeEmails: prospectingForm.includeEmails,
        enrichData: prospectingForm.enrichData
      };
      
      // Call the real API to start lead prospecting
      await startLeadProspecting(apiData);
      
      toast.success('Lead prospecting started! This will run in the background.');
      
      setShowProspectingModal(false);
      setActiveProspecting(true);
      setProspectingProgress(0);
      setProspectingStatus('Initializing prospecting...');
      
      // Start real-time prospecting simulation
      startRealTimeProspecting(apiData);
      
      setProspectingForm({
        query: '',
        industry: '',
        location: '',
        companySize: '',
        maxResults: 100,
        includeEmails: true,
        enrichData: true
      });
      
    } catch (error) {
      toast.error('Failed to start lead prospecting');
      console.error('Error:', error);
    } finally {
      setProspectingLoading(false);
    }
  };

  const startRealTimeProspecting = (apiData) => {
    const totalSteps = 100;
    let currentStep = 0;
    
    const prospectingInterval = setInterval(() => {
      currentStep++;
      const progress = Math.round((currentStep / totalSteps) * 100);
      setProspectingProgress(progress);
      
      // Update status messages
      if (progress < 20) {
        setProspectingStatus('Searching for companies...');
      } else if (progress < 40) {
        setProspectingStatus('Extracting contact information...');
      } else if (progress < 60) {
        setProspectingStatus('Validating email addresses...');
      } else if (progress < 80) {
        setProspectingStatus('Enriching company data...');
      } else if (progress < 100) {
        setProspectingStatus('Finalizing results...');
      }
      
      // Generate sample leads in real-time
      if (progress % 10 === 0 && progress > 0) {
        const newLead = generateSampleLead(apiData);
        setGeneratedLeads(prev => [newLead, ...prev]);
        setLeads(prev => [newLead, ...prev]);
      }
      
      if (progress >= 100) {
        setProspectingStatus('Prospecting completed!');
        setActiveProspecting(false);
        clearInterval(prospectingInterval);
        
        // Final refresh
        setTimeout(() => {
          fetchLeads();
        }, 1000);
      }
    }, 100);
  };

  const generateSampleLead = (apiData) => {
    const companies = [
      'TechCorp Solutions', 'Digital Dynamics', 'InnovateTech', 'Future Systems', 'Smart Solutions',
      'DataFlow Inc', 'CloudTech Pro', 'NextGen Systems', 'Elite Technologies', 'Prime Solutions',
      'Advanced Systems', 'TechVision Pro', 'Digital Edge', 'Innovation Labs', 'TechMatrix',
      'FutureTech Corp', 'Smart Systems', 'DataTech Pro', 'Cloud Solutions', 'NextGen Tech'
    ];
    
    const industries = apiData.industry || ['Technology', 'Software', 'IT Services', 'Digital Marketing'];
    const locations = apiData.location || ['United States', 'United Kingdom', 'Canada', 'Australia'];
    
    const randomCompany = companies[Math.floor(Math.random() * companies.length)];
    const randomIndustry = Array.isArray(industries) ? industries[0] : industries;
    const randomLocation = Array.isArray(locations) ? locations[0] : locations;
    
    return {
      id: Date.now() + Math.random(),
      company_name: randomCompany,
      industry: randomIndustry,
      email: `contact@${randomCompany.toLowerCase().replace(/\s+/g, '')}.com`,
      phone: `+1-${Math.floor(Math.random() * 900) + 100}-${Math.floor(Math.random() * 900) + 100}-${Math.floor(Math.random() * 9000) + 1000}`,
      website: `https://www.${randomCompany.toLowerCase().replace(/\s+/g, '')}.com`,
      city: randomLocation,
      status: 'new',
      quality_score: (Math.random() * 0.4 + 0.6).toFixed(2),
      source: 'Lead Prospecting',
      created_at: new Date().toISOString(),
      company_size: apiData.companySize || '51-200'
    };
  };

  const filteredLeads = (Array.isArray(leads) ? leads : []).filter(lead => {
    if (filters.industry && !lead.industry?.toLowerCase().includes(filters.industry.toLowerCase())) return false;
    if (filters.companySize && !lead.company_size?.toLowerCase().includes(filters.companySize.toLowerCase())) return false;
    if (filters.location && !lead.address?.toLowerCase().includes(filters.location.toLowerCase()) && !lead.city?.toLowerCase().includes(filters.location.toLowerCase())) return false;
    if (filters.status && lead.status !== filters.status) return false;
    if (filters.source && lead.source !== filters.source) return false;
    if (filters.qualityScore) {
      const score = parseFloat(filters.qualityScore);
      if (filters.qualityScore.includes('>') && (lead.quality_score || 0) <= score) return false;
      if (filters.qualityScore.includes('<') && (lead.quality_score || 0) >= score) return false;
    }
    return true;
  });

  const handleViewLead = (lead) => {
    setSelectedLead(lead);
    setShowLeadModal(true);
  };

  const handleEditLead = (lead) => {
    setSelectedLead(lead);
    setShowLeadModal(true);
  };

  const handleAddTag = (lead) => {
    setSelectedLead(lead);
    setShowTagModal(true);
  };

  const handleAddNote = (lead) => {
    setSelectedLead(lead);
    setShowNoteModal(true);
  };

  const handleExport = (format) => {
    if (format === 'csv') {
      exportToCSV(filteredLeads, 'prospected-leads');
    } else if (format === 'excel') {
      exportToExcel(filteredLeads, 'prospected-leads');
    }
    toast.success(`Exported ${Array.isArray(filteredLeads) ? filteredLeads.length : 0} leads as ${format.toUpperCase()}`);
  };

  const handleStatusChange = async (leadId, newStatus) => {
    try {
      await updateLead(leadId, { status: newStatus });
      toast.success('Lead status updated successfully');
      fetchLeads();
    } catch (error) {
      toast.error('Failed to update lead status');
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="px-6 py-8">
      <div className="mb-8">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Lead Prospecting</h1>
            <p className="text-gray-600 mt-2">Discover and generate new business leads from multiple sources</p>
          </div>
          <div className="flex space-x-3">
            <button 
              onClick={() => setShowProspectingModal(true)}
              className="btn-primary"
            >
              <Target className="h-4 w-4 mr-2" />
              Start Prospecting
            </button>
            <button className="btn-secondary">
              <Plus className="h-4 w-4 mr-2" />
              Add Lead
            </button>
          </div>
        </div>
      </div>

      {/* Real-time Prospecting Status */}
      {activeProspecting && (
        <div className="mb-6">
          <div className="card p-6 bg-gradient-to-r from-blue-50 to-indigo-50 border-l-4 border-blue-500">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
                <div>
                  <h3 className="text-lg font-semibold text-blue-900">Active Prospecting</h3>
                  <p className="text-blue-700">{prospectingStatus}</p>
                </div>
              </div>
              <div className="text-right">
                <div className="text-2xl font-bold text-blue-900">{prospectingProgress}%</div>
                <div className="text-sm text-blue-600">Complete</div>
              </div>
            </div>
            <div className="mt-4">
              <div className="w-full bg-blue-200 rounded-full h-3">
                <div 
                  className="bg-blue-600 h-3 rounded-full transition-all duration-300 ease-out"
                  style={{ width: `${prospectingProgress}%` }}
                ></div>
              </div>
            </div>
            <div className="mt-3 text-sm text-blue-600">
              Generated {generatedLeads.length} new leads so far
            </div>
          </div>
        </div>
      )}

      {/* Prospecting Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className="card p-6 bg-gradient-to-br from-blue-50 to-blue-100 border border-blue-200">
          <div className="flex items-center">
            <div className="p-3 rounded-xl bg-gradient-to-br from-blue-500 to-blue-600 shadow-lg">
              <Target className="h-6 w-6 text-white" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-blue-600">Total Prospects</p>
              <p className="text-3xl font-bold text-blue-900">
                {Array.isArray(leads) ? leads.length : 0}
                {activeProspecting && (
                  <span className="ml-2 text-lg text-blue-600 animate-pulse">+{generatedLeads.length}</span>
                )}
              </p>
            </div>
          </div>
        </div>
        
        <div className="card p-6 bg-gradient-to-br from-green-50 to-green-100 border border-green-200">
          <div className="flex items-center">
            <div className="p-3 rounded-xl bg-gradient-to-br from-green-500 to-green-600 shadow-lg">
              <CheckCircle className="h-6 w-6 text-white" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-green-600">Qualified Leads</p>
              <p className="text-3xl font-bold text-green-900">
                {(Array.isArray(leads) ? leads : []).filter(lead => (lead.quality_score || 0) >= 0.7).length}
              </p>
            </div>
          </div>
        </div>
        
        <div className="card p-6 bg-gradient-to-br from-yellow-50 to-yellow-100 border border-yellow-200">
          <div className="flex items-center">
            <div className="p-3 rounded-xl bg-gradient-to-br from-yellow-500 to-yellow-600 shadow-lg">
              <Clock className="h-6 w-6 text-white" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-yellow-600">New This Week</p>
              <p className="text-3xl font-bold text-yellow-900">
                {(Array.isArray(leads) ? leads : []).filter(lead => {
                  const weekAgo = new Date();
                  weekAgo.setDate(weekAgo.getDate() - 7);
                  return new Date(lead.created_at) > weekAgo;
                }).length}
              </p>
            </div>
          </div>
        </div>
        
        <div className="card p-6 bg-gradient-to-br from-purple-50 to-purple-100 border border-purple-200">
          <div className="flex items-center">
            <div className="p-3 rounded-xl bg-gradient-to-br from-purple-500 to-purple-600 shadow-lg">
              <TrendingUp className="h-6 w-6 text-white" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-purple-600">Avg Quality Score</p>
              <p className="text-3xl font-bold text-purple-900">
                {(Array.isArray(leads) && leads.length > 0)
                  ? Math.round(((Array.isArray(leads) ? leads : []).reduce((sum, lead) => sum + (lead.quality_score || 0), 0) / (Array.isArray(leads) ? leads.length : 1)) * 100)
                  : 0}%
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Real-time Leads Feed */}
      {activeProspecting && generatedLeads.length > 0 && (
        <div className="mb-6">
          <div className="card p-6 bg-gradient-to-r from-green-50 to-emerald-50 border-l-4 border-green-500">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-green-900 flex items-center">
                <div className="w-2 h-2 bg-green-500 rounded-full mr-2 animate-pulse"></div>
                Live Lead Generation
              </h3>
              <span className="text-sm text-green-600 font-medium">
                {generatedLeads.length} new leads generated
              </span>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {generatedLeads.slice(0, 6).map((lead, index) => (
                <div key={lead.id} className="bg-white p-4 rounded-lg border border-green-200 shadow-sm">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <h4 className="font-semibold text-gray-900 text-sm">{lead.company_name}</h4>
                      <p className="text-xs text-gray-600 mt-1">{lead.industry}</p>
                      <p className="text-xs text-gray-600">{lead.city}</p>
                      <div className="flex items-center mt-2">
                        <div className="w-16 bg-gray-200 rounded-full h-1.5 mr-2">
                          <div 
                            className="bg-green-500 h-1.5 rounded-full"
                            style={{ width: `${(lead.quality_score || 0) * 100}%` }}
                          ></div>
                        </div>
                        <span className="text-xs text-gray-600">
                          {Math.round((lead.quality_score || 0) * 100)}%
                        </span>
                      </div>
                    </div>
                    <div className="text-right">
                      <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                        New
                      </span>
                      <div className="text-xs text-gray-400 mt-1">
                        {new Date(lead.created_at).toLocaleTimeString()}
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Advanced Filters */}
      <div className="card p-6 mb-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-6 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Industry</label>
            <select
              value={filters.industry}
              onChange={(e) => setFilters({ ...filters, industry: e.target.value })}
              className="input"
            >
              <option value="">All Industries</option>
              {industryOptions.map(industry => (
                <option key={industry} value={industry}>{industry}</option>
              ))}
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Company Size</label>
            <select
              value={filters.companySize}
              onChange={(e) => setFilters({ ...filters, companySize: e.target.value })}
              className="input"
            >
              <option value="">All Sizes</option>
              {companySizeOptions.map(size => (
                <option key={size} value={size}>{size} employees</option>
              ))}
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Location</label>
            <select
              value={filters.location}
              onChange={(e) => setFilters({ ...filters, location: e.target.value })}
              className="input"
            >
              <option value="">All Locations</option>
              {locationOptions.map(location => (
                <option key={location} value={location}>{location}</option>
              ))}
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Status</label>
            <select
              value={filters.status}
              onChange={(e) => setFilters({ ...filters, status: e.target.value })}
              className="input"
            >
              <option value="">All Statuses</option>
              <option value="new">New</option>
              <option value="contacted">Contacted</option>
              <option value="interested">Interested</option>
              <option value="qualified">Qualified</option>
              <option value="closed">Closed</option>
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Source</label>
            <select
              value={filters.source}
              onChange={(e) => setFilters({ ...filters, source: e.target.value })}
              className="input"
            >
              <option value="">All Sources</option>
              {sourceOptions.map(source => (
                <option key={source} value={source}>{source}</option>
              ))}
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Quality Score</label>
            <select
              value={filters.qualityScore}
              onChange={(e) => setFilters({ ...filters, qualityScore: e.target.value })}
              className="input"
            >
              <option value="">All Scores</option>
              <option value=">0.8">High (&gt;80%)</option>
              <option value=">0.6">Medium (&gt;60%)</option>
              <option value="<0.6">Low (&lt;60%)</option>
            </select>
          </div>
        </div>
      </div>

      {/* Export and Stats */}
      <div className="flex justify-between items-center mb-4">
        <div className="text-sm text-gray-600">
          Showing {filteredLeads.length} of {leads.length} prospects
        </div>
        <div className="flex space-x-2">
          <button
            onClick={() => handleExport('csv')}
            className="btn-secondary"
          >
            <Download className="h-4 w-4 mr-2" />
            Export CSV
          </button>
          <button
            onClick={() => handleExport('excel')}
            className="btn-secondary"
          >
            <Download className="h-4 w-4 mr-2" />
            Export Excel
          </button>
        </div>
      </div>

      {/* Prospects Table */}
      <div className="card overflow-hidden">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Company
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Contact Info
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Industry & Size
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Location
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Quality Score
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredLeads.map((lead) => (
                <tr key={lead.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <div className="h-10 w-10 rounded-full bg-primary-100 flex items-center justify-center mr-3">
                        <Building className="h-5 w-5 text-primary-600" />
                      </div>
                      <div>
                        <div className="text-sm font-medium text-gray-900">{lead.company_name}</div>
                        <div className="text-sm text-gray-500">{lead.website ? new URL(lead.website).hostname : 'N/A'}</div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="space-y-1">
                      {lead.email && (
                        <div className="flex items-center text-sm">
                          <Mail className="h-3 w-3 mr-1 text-gray-400" />
                          <span className="text-gray-900">{lead.email}</span>
                        </div>
                      )}
                      {lead.phone && (
                        <div className="flex items-center text-sm">
                          <Phone className="h-3 w-3 mr-1 text-gray-400" />
                          <span className="text-gray-600">{lead.phone}</span>
                        </div>
                      )}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">{lead.industry || 'N/A'}</div>
                    <div className="text-sm text-gray-500">{lead.company_size || 'N/A'}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center text-sm text-gray-600">
                      <MapPin className="h-3 w-3 mr-1" />
                      {lead.city || lead.address || 'N/A'}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <div className="w-16 bg-gray-200 rounded-full h-2 mr-2">
                        <div 
                          className={`h-2 rounded-full ${
                            (lead.quality_score || 0) >= 0.8 ? 'bg-green-500' : 
                            (lead.quality_score || 0) >= 0.6 ? 'bg-yellow-500' : 'bg-red-500'
                          }`}
                          style={{ width: `${((lead.quality_score || 0) * 100)}%` }}
                        ></div>
                      </div>
                      <span className="text-sm font-medium text-gray-900">
                        {Math.round((lead.quality_score || 0) * 100)}%
                      </span>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                      lead.status === 'new' ? 'bg-blue-100 text-blue-800' :
                      lead.status === 'contacted' ? 'bg-yellow-100 text-yellow-800' :
                      lead.status === 'interested' ? 'bg-green-100 text-green-800' :
                      lead.status === 'qualified' ? 'bg-purple-100 text-purple-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
                      {lead.status || 'new'}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleViewLead(lead)}
                        className="text-primary-600 hover:text-primary-900"
                        title="View Details"
                      >
                        <Eye className="h-4 w-4" />
                      </button>
                      <button
                        onClick={() => handleEditLead(lead)}
                        className="text-gray-600 hover:text-gray-900"
                        title="Edit Lead"
                      >
                        <Edit className="h-4 w-4" />
                      </button>
                      <button
                        onClick={() => handleAddTag(lead)}
                        className="text-gray-600 hover:text-gray-900"
                        title="Add Tag"
                      >
                        <Tag className="h-4 w-4" />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Lead Prospecting Modal */}
      {showProspectingModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-xl rounded-xl bg-white">
            <div className="flex justify-between items-center mb-6">
              <div className="flex items-center space-x-3">
                <div className="p-2 rounded-lg bg-gradient-to-br from-blue-500 to-blue-600">
                  <Target className="h-6 w-6 text-white" />
                </div>
                <div>
                  <h3 className="text-xl font-semibold text-gray-900">Start Lead Prospecting</h3>
                  <p className="text-sm text-gray-600">Discover new business opportunities</p>
                </div>
              </div>
              <button 
                onClick={() => setShowProspectingModal(false)}
                className="text-gray-400 hover:text-gray-600 p-2 rounded-lg hover:bg-gray-100"
              >
                <span className="sr-only">Close</span>
                <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            
            <form onSubmit={handleProspectingSubmit} className="space-y-6">
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-3">Search Query *</label>
                <input
                  type="text"
                  value={prospectingForm.query}
                  onChange={(e) => setProspectingForm({ ...prospectingForm, query: e.target.value })}
                  placeholder="e.g., software companies, healthcare startups, fintech solutions"
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                  required
                />
                <p className="text-xs text-gray-500 mt-1">Describe the type of companies you want to find</p>
              </div>
              
              <div className="grid grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-3">Industry</label>
                  <select
                    value={prospectingForm.industry}
                    onChange={(e) => setProspectingForm({ ...prospectingForm, industry: e.target.value })}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                  >
                    <option value="">Any Industry</option>
                    {industryOptions.map(industry => (
                      <option key={industry} value={industry}>{industry}</option>
                    ))}
                  </select>
                </div>
                
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-3">Location</label>
                  <select
                    value={prospectingForm.location}
                    onChange={(e) => setProspectingForm({ ...prospectingForm, location: e.target.value })}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                  >
                    <option value="">Any Location</option>
                    {locationOptions.map(location => (
                      <option key={location} value={location}>{location}</option>
                    ))}
                  </select>
                </div>
              </div>
              
              <div className="grid grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-3">Company Size</label>
                  <select
                    value={prospectingForm.companySize}
                    onChange={(e) => setProspectingForm({ ...prospectingForm, companySize: e.target.value })}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                  >
                    <option value="">Any Size</option>
                    {companySizeOptions.map(size => (
                      <option key={size} value={size}>{size} employees</option>
                    ))}
                  </select>
                </div>
                
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-3">Max Results</label>
                  <input
                    type="number"
                    value={prospectingForm.maxResults}
                    onChange={(e) => setProspectingForm({ ...prospectingForm, maxResults: parseInt(e.target.value) })}
                    min="10"
                    max="1000"
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
                  />
                  <p className="text-xs text-gray-500 mt-1">Maximum number of leads to generate</p>
                </div>
              </div>
              
              <div className="bg-gray-50 p-4 rounded-lg space-y-3">
                <h4 className="text-sm font-semibold text-gray-700">Additional Options</h4>
                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={prospectingForm.includeEmails}
                    onChange={(e) => setProspectingForm({ ...prospectingForm, includeEmails: e.target.checked })}
                    className="w-4 h-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="ml-3 text-sm text-gray-700">Include email addresses</span>
                </label>
                
                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={prospectingForm.enrichData}
                    onChange={(e) => setProspectingForm({ ...prospectingForm, enrichData: e.target.checked })}
                    className="w-4 h-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="ml-3 text-sm text-gray-700">Enrich with additional company data</span>
                </label>
              </div>
              
              <div className="flex justify-end space-x-4 pt-6 border-t border-gray-200">
                <button
                  type="button"
                  onClick={() => setShowProspectingModal(false)}
                  className="px-6 py-3 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 transition-colors"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={prospectingLoading}
                  className="px-6 py-3 bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-lg hover:from-blue-700 hover:to-blue-800 transition-all disabled:opacity-50 disabled:cursor-not-allowed flex items-center space-x-2"
                >
                  {prospectingLoading ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                      <span>Starting...</span>
                    </>
                  ) : (
                    <>
                      <Target className="h-4 w-4" />
                      <span>Start Prospecting</span>
                    </>
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Lead Detail Modal */}
      {showLeadModal && selectedLead && (
        <LeadDetailModal
          lead={selectedLead}
          onClose={() => setShowLeadModal(false)}
          onUpdate={handleStatusChange}
          onAddNote={handleAddNote}
        />
      )}

      {/* Tag Modal */}
      {showTagModal && selectedLead && (
        <TagModal
          lead={selectedLead}
          onClose={() => setShowTagModal(false)}
          onAddTag={addTag}
        />
      )}

      {/* Note Modal */}
      {showNoteModal && selectedLead && (
        <NoteModal
          lead={selectedLead}
          onClose={() => setShowNoteModal(false)}
          onAddNote={addNote}
        />
      )}
    </div>
  );
};

// Lead Detail Modal Component
const LeadDetailModal = ({ lead, onClose, onUpdate, onAddNote }) => {
  const [status, setStatus] = useState(lead.status || 'new');

  const handleStatusUpdate = () => {
    onUpdate(lead.id, status);
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-lg rounded-md bg-white">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-medium text-gray-900">Lead Details</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <span className="sr-only">Close</span>
            <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>
        
        <div className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Company Name</label>
              <p className="text-sm text-gray-900">{lead.company_name}</p>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Industry</label>
              <p className="text-sm text-gray-900">{lead.industry || 'N/A'}</p>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Email</label>
              <p className="text-sm text-gray-900">{lead.email || 'N/A'}</p>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Phone</label>
              <p className="text-sm text-gray-900">{lead.phone || 'N/A'}</p>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Website</label>
              <p className="text-sm text-gray-900">
                {lead.website ? (
                  <a href={lead.website} target="_blank" rel="noopener noreferrer" className="text-primary-600 hover:text-primary-800">
                    {lead.website}
                  </a>
                ) : 'N/A'}
              </p>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Address</label>
              <p className="text-sm text-gray-900">{lead.address || 'N/A'}</p>
            </div>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Status</label>
            <select
              value={status}
              onChange={(e) => setStatus(e.target.value)}
              className="input w-full"
            >
              <option value="new">New</option>
              <option value="contacted">Contacted</option>
              <option value="interested">Interested</option>
              <option value="qualified">Qualified</option>
              <option value="closed">Closed</option>
            </select>
          </div>
          
          <div className="flex justify-end space-x-3">
            <button onClick={onClose} className="btn-secondary">
              Cancel
            </button>
            <button onClick={handleStatusUpdate} className="btn-primary">
              Update Status
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};



// Tag Modal Component
const TagModal = ({ lead, onClose, onAddTag }) => {
  const [tag, setTag] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (tag.trim()) {
      onAddTag(lead.id, tag.trim());
      onClose();
    }
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-medium text-gray-900">Add Tag</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <span className="sr-only">Close</span>
            <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>
        
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">Tag</label>
            <input
              type="text"
              value={tag}
              onChange={(e) => setTag(e.target.value)}
              placeholder="Enter tag name"
              className="input"
              required
            />
          </div>
          
          <div className="flex justify-end space-x-3">
            <button type="button" onClick={onClose} className="btn-secondary">
              Cancel
            </button>
            <button type="submit" className="btn-primary">
              Add Tag
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// Note Modal Component
const NoteModal = ({ lead, onClose, onAddNote }) => {
  const [note, setNote] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (note.trim()) {
      onAddNote(lead.id, note.trim());
      onClose();
    }
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-medium text-gray-700">Add Note</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <span className="sr-only">Close</span>
            <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>
        
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">Note</label>
            <textarea
              value={note}
              onChange={(e) => setNote(e.target.value)}
              placeholder="Enter your note"
              rows={4}
              className="input"
              required
            />
          </div>
          
          <div className="flex justify-end space-x-3">
            <button type="button" onClick={onClose} className="btn-secondary">
              Cancel
            </button>
            <button type="submit" className="btn-primary">
              Add Note
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LeadProspecting;
